mapper.update:
update recruit_apply_info
roleId=#{roleId}, memberId=#{memberId}, resumeId=#{resumeId}, videoUuid=#{videoUuid}, createDate=#{createDate}, updateDate=#{updateDate}, status=#{status}, level=#{level}, videoStatus=#{videoStatus}, optional=#{optional}, completion=#{completion}, isView=#{isView}, isContact=#{isContact}, isVideo=#{isVideo},
where id=#{id}

mapper.select:
   select 
 
   from recruit_apply_info
  
			
				and id=#{id} 
			
			
				and roleId=#{roleId} 
			
			
				and memberId=#{memberId} 
			
			
				and resumeId=#{resumeId} 
			
			
				and videoUuid=#{videoUuid} 
			
			
				and createDate=#{createDate} 
			
			
				and updateDate=#{updateDate} 
			
			
				and status=#{status} 
			
			
				and level=#{level} 
			
			
				and videoStatus=#{videoStatus} 
			
			
				and optional=#{optional} 
			
			
				and completion=#{completion} 
			
			
				and isView=#{isView} 
			
			
				and isContact=#{isContact} 
			
			
				and isVideo=#{isVideo}

		select
		 
		
			from recruit_apply_info
		
		
				
					and id=#{id} 
				
				
					and roleId=#{roleId} 
				
				
					and memberId=#{memberId} 
				
				
					and resumeId=#{resumeId} 
				
				
					and videoUuid=#{videoUuid} 
				
				
					and createDate < #{createDate} 
				
				
					and updateDate=#{updateDate} 
				
				
					and status=#{status} 
				
				
					and level=#{level} 
				
				
					and videoStatus=#{videoStatus} 
				
				
					and roleId in (select r.id from recruit_role_info r, recruit_info i, member_info m 
					where r.recruitId = i.id and i.memberId = m.id and m.nickname = #{publishName}) 
				
				
					and roleId in (select id from recruit_role_info where name = #{roleName})
				
				
					and roleId in (select a.id from recruit_role_info a , recruit_info b 
					where a.recruitId = b.id and b.title = #{recruitName})
				
				
					and optional=#{optional} 
				
				
					and completion=#{completion} 
				
				
					and level in ('1','2') 
				
				
					and level in ('3','4','5') 
				
				
					and memberId in (select id from member_info where realName = #{realname}) 
				
				
					and memberId in (select id from member_info where nickname = #{nickname}) 
				
				
					and memberId in (select id from member_info where identityStatus = '1') 
				
				
					and memberId in (select id from member_info where identityStatus != '1') 
				
				
					and isView=#{isView} 
				
				
					and isContact=#{isContact} 
				
				
					and isVideo=#{isVideo} 
				
				
					and resumeId in
					
  						#{resumeId}
 					
				
		
		
				order by ${sort} ${order}

select count(id) from recruit_apply_info

		
			and id=#{id} 
		
		
			and roleId=#{roleId} 
		
		
			and memberId=#{memberId} 
		
		
			and resumeId=#{resumeId} 
		
		
			and videoUuid=#{videoUuid} 
		
		
			and createDate=#{createDate} 
		
		
			and updateDate=#{updateDate} 
		
		
			and status=#{status} 
		
		
			and level=#{level} 
		
		
			and videoStatus=#{videoStatus} 
		
		
			and optional=#{optional} 
		
		
			and completion=#{completion} 
		
		
			and isView=#{isView} 
		
		
			and isContact=#{isContact} 
		
		
			and isVideo=#{isVideo}

		select
		 
		 from recruit_apply_info where id in
		
  			#{idList}
